        commit(types.SET_SPARKLINE_CHART, { fsym, tsym, chart });
      });
    },


}

// mutations
const mutations = {
  [types.SET_CHART](state, { fsym, tsym, interval, chart }) {
    console.log(`Setting chart of length ${chart.length}: ${fsym}/${tsym} over ${interval}`)
